templates.content:
@{
    var highlighMdl = Model.GetModule("productHighlight");
    var ourTeamMdl = Model.GetModule("ourTeam");
    var contactUsMdl = Model.GetModule("workWithUs");
    var latestBlogPost = Model.GetModule("latestBlogPosts");

}

<partial name="../Layouts/MainNavigation.cshtml"/>
<partial name="../Widgets/PageHeroBanner.cshtml"/>
  
<div class="main main-raised">
    <div class="section section-basic">
        <div class="container">
            @if(highlighMdl != null){
                <partial name="@highlighMdl.Template.FilePath" model="highlighMdl"/>
            }
            @if(ourTeamMdl != null){
                <partial name="@ourTeamMdl.Template.FilePath" model="ourTeamMdl"/>
            }
            @if(contactUsMdl != null){
                <partial name="@contactUsMdl.Template.FilePath" model="contactUsMdl"/>
            }
            <!-- @Html.Raw(Model.Content) -->
        </div>
    </div>
</div>

<partial name="../Widgets/LatestBlogPosts.cshtml" model="latestBlogPost"/>
<partial name="../Layouts/Footer.cshtml"/>

@inject MixCmsContext context
@{
  var postRepo = PostContentViewModel.GetRootRepository(context);
   var posts = await postRepo.GetPagingAsync(m=> true, new PagingModel()
    {
        SortBy = "CreatedDateTime",
        SortDirection = SortDirection.Desc,
        PageSize = 3,
        PageIndex = 0
    });

}
<div class="section">
    <div class="container">
      <div class="row">
        <div class="col-md-12">
          <h2 class="title text-center">Latest Blog Posts</h2>
          <br>
          <div class="row">
            <!-- @@foreach(var item in Model.Posts.Items.Take(4)){ -->
            @foreach(var item in posts.Items){
            <div class="col-md-4">
              <div class="card card-blog">
                <div class="card-header card-header-image">
                  <a href="/post/@item.Id/@(item.SeoName)">
                    <img class="img img-raised" src="@item.Image">
                  </a>
                </div>
                <div class="card-body">
                  <h6 class="category text-info">
                        @*@foreach(var cat in item.ListCategory){
                          @cat
                        }*@
                    </h6>  
                  <h4 class="card-title">
                    <a href="/post/@item.Id/@(item.SeoName)">@item.Title</a> 
                  </h4>
                  <p class="card-description">
                    @if (!string.IsNullOrEmpty(item.Excerpt))
                      @(item.Excerpt.Length > 160 ? item.Excerpt.Substring(0,160) + "..." : item.Excerpt)
                      <a href="/post/@item.Id/@(item.SeoName)"> Read More </a>
                    
                  </p>
                </div>
              </div>
            </div>
            }
          </div>
          
        </div>
      </div>
    </div>
  </div>

@{ 
  string bgUrl = Model.Property<string>("bannerImgSrc");
  string h1Text = Model.Property<string>("bannerH1Text");
  string description = Model.Property<string>("bannerDescription");
  string ctaText = Model.Property<string>("bannerCtaText"); 
  string ico = Model.Property<string>("bannerCtaIcon"); 
  string ctaUrl = Model.Property<string>("bannerCtaUrl"); 
}
<div class="page-header header-filter" data-parallax="true" style="background-image: url('@bgUrl')">
    <div class="container">
      <div class="row">
        <div class="col-md-6">
          <h1 class="title">@h1Text</h1>
          <h4>@description</h4>
          <br>
          <a href="@ctaUrl" target="_blank" class="btn btn-danger btn-raised btn-lg">
            <i class="@ico"></i> @ctaText 
          </a>
        </div>
      </div>
    </div>
  </div>

@{ 
  string bgUrl = Model.Image;
}

<div class="page-header header-filter" data-parallax="true" style="background-image: url('@bgUrl');"></div>
